import { useEffect, useState } from "react";
import styles from "../Form/Form.module.css";

export default function Form(props) {
  const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

  const passwordValidate = /^(?=.*d)(?=.*[a-z])(?=.*[A-Z]).{6,20}$/;

  const validate = (inputs) => {
    const errors = {};
    if (!regexEmail.test(inputs.userName)) {
      errors.userName = "Debe ser un email";
    }
    if (!inputs.userName) {
      errors.userName = "Este campo no puede estar vacio";
    }
    if (inputs.userName.length > 35) {
      errors.userName = "No puede tener mas de 35 caracteres";
    }

    if (!passwordValidate.test(inputs.password)) {
      errors.password = "Debe contener minimo un número";
    }
    if (inputs.password.length < 6 || inputs.password.length > 20) {
      errors.password = "Debe tener mínimo 6 y máximo 20 caracteres";
    }
    return errors;
  };

  const [userData, setUserData] = useState({
    userName: "",
    password: "",
  });

  const [errors, setErrors] = useState({
    userName: "",
    password: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setUserData({
      ...userData,
      [name]: value,
    });
    setErrors(
      validate({
        ...userData,
        [name]: value,
      })
    );
  };

  useEffect(() => {
    console.log(userData);
  }, [userData]);

  return (
    <form className={styles.container}>
      <img
        src="https://cdn.hobbyconsolas.com/sites/navi.axelspringer.es/public/media/image/2019/05/rick-morty-temporada-4-llegara-noviembre.jpg?tf=3840x"
        alt=""
      />
      <br />
      {errors.userName ? <p>{errors.userName}</p> : null}
      <label htmlFor="">Username: </label>
      <input
        className={styles.formulario}
        type="text"
        value={userData.userName}
        name="userName"
        placeholder="Ingrese su email"
        onChange={handleChange}
      />
      {errors.password ? <p>{errors.password}</p> : null}
      <label htmlFor="">Password: </label>
      <input
        className={styles.formulario}
        type="password"
        value={userData.password}
        name="password"
        placeholder="Ingrese su password"
        onChange={handleChange}
      />
      <br />
      <button className={styles.buttonlogin}>Login</button>
    </form>
  );
}
